class Solution {
public:// Aliases
    using Row = std::vector<int>;
    using Matrix = std::vector<Row>;
    // row, col, path-length, cost
    using Blob = std::tuple<int, int, int, int, int, int>;
    using Result = std::vector<Blob>;
    using Queue = std::queue<Blob>;

public: // Constants, enums
    const int cDR[4]{-1, 0, 1, 0};
    const int cDC[4]{0, 1, 0, -1};

public: // Data
    Matrix _cost;

public:
    void print(const Blob& b) const {
        int pr = std::get<0>(b);
        int pc = std::get<1>(b);
        int cr = std::get<2>(b);
        int cc = std::get<3>(b);
        int len = std::get<4>(b);
        int cost = std::get<5>(b);

        std::cout << "[(" << pr << ", " << pc << ") -> (" << cr << ", " << cc << "), " << len << ", " << cost << "]" << std::endl;
    }

    void print(const std::string& msg, int r, int c, int val, const Matrix& matrix) const {
        std::cout << msg << " [" << r << ", " << c << "] val " << matrix[r][c] << " != " << val << " --" << std::endl;
        print(matrix);
    }

    void print(const std::string& msg, const Matrix& matrix) const {
        std::cout << msg << " --" << std::endl;
        print(matrix);
    }

    void print(const Matrix& matrix) const {
        for (int i = 0; i < matrix.size(); i++) {
            for (int j = 0; j < matrix[i].size(); j++) {
                if (matrix[i][j] == matrix.size() * matrix[i].size()) {
                    std::cout << std::setw(4) << "-" << " ";
                } else {
                    std::cout << std::setw(4) << matrix[i][j] << " ";
                }
            }
            std::cout << std::endl;
        }
    }

    int shortestPath(std::vector<std::vector<int>>& grid, int k) {
        int maxRows = grid.size();
        int maxCols = grid[0].size();
        _cost.resize(maxRows);
        for (int r = 0; r < maxRows; r++) {
            _cost[r].resize(maxCols);
            for (int c = 0; c < maxCols; c++) {
                _cost[r][c] = maxRows * maxCols;
            }
        }

        Queue q;
        q.emplace(Blob(0, 0, 0, 0, 0, 0));

        while (! q.empty()) {
            Blob b = q.front();
            q.pop();
//            print(b);
            int pr = std::get<0>(b);
            int pc = std::get<1>(b);
            int cr = std::get<2>(b);
            int cc = std::get<3>(b);
            int clen = std::get<4>(b);
            int ccost = std::get<5>(b);

            if (ccost > k) {
                continue;
            }

            // End of search
            if (cr == maxRows - 1 && cc == maxCols - 1) {
                return clen;
            }

            if (_cost[cr][cc] <= ccost) {
                continue;
            }

            _cost[cr][cc] = ccost;

            for (int i = 0; i < 4; i++) {
                int nr = cr + cDR[i];
                int nc = cc + cDC[i];
                if (nr < 0 || nr >= maxRows ||
                    nc < 0 || nc >= maxCols ||
                    (nr == pr && nc == pc)) {
                    continue;
                }

                int nlen = clen + 1;
                int ncost = ccost + grid[nr][nc];
                q.emplace(Blob(cr, cc, nr, nc, nlen, ncost));
            }
        }
//        print("grid", grid);
//        print("cost", _cost);
        return -1;
    }
};

void runtest(Matrix grid, int k, int res) {
    Solution sol;
    int len = sol.shortestPath(grid, k);
    if (len == res) {
        std::cout << "PASS [" << len << "==" << res << "]";
    } else {
        std::cout << "FAIL [" << len << "!=" << res << "]";
    }
    std::cout << std::endl;
}

